import * as api from './api'

export function getProfile({ token }){
  return api.get(`/profile`, {}, token)
}

export function createProfile ({ profile, token }) {
  return api.post('/profile', profile, token)
}

export function updateProfile ({ profile, token }) {
  return api.put(`/profile`, profile, token)
}

export function deleteProfile ({ token }) {
  return api.del(`/profile`, token)
}